mapper.update:
	UPDATE `t_sys_dept` 
	SET 	
      `name`=#{name},parentId=#{parentId}, deptLevel=#{deptLevel}, `memo`=#{memo} ,
      `editor`=#{editor}, `editTime`=NOW(), delete_flag=#{delete_flag}
   WHERE  
   	  id = #{id}

mapper.select:
select bb.* from (SELECT a.* from t_sys_dept a join `t_sys_user` b on a.`code` = b.dept and b.username=#{param1}) aa join t_sys_dept bb on aa.code = bb.pcode or aa.code = bb.code
bb.areaId = #{param2}
order by bb.grade,bb.code

